style:
body {
    margin: 0;
    font-family: 'Roboto', 'Noto', sans-serif;
    background-color: #eee;
}

app-toolbar {
    background-color: #2c5986;
    color: #fff;
}

app-drawer-layout {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}

app-drawer {
    --app-drawer-content-container: {
        background-color: #fff;
        overflow: auto;
    }
}

.paper-item-link {
    color: #000;
    text-decoration: none;
}

.editor-wrapper {
	background-color: #2D2D2D;
    position: absolute;
    top: 64px;
    left: 0;
    right: 0;
    bottom: 0;
}

juicy-ace-editor {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}

paper-material {
    display: inline-block;
    background: white;
    box-sizing: border-box;
    margin: 8px;
    padding: 16px;
    border-radius: 2px;
    width: calc(100% - 16px);
}

paper-item {
    font-size: 9pt;
}

script:
var app = document.querySelector('#app');
var form = document.querySelector('#form');
var endDrawer = document.querySelector('#endDrawer');

app.selected = 0;

app.save = function save() {
    form.submit();
};

app.url = function url(type, id) {
    return '#/' + type + '/' + id;
};

app.showEndDrawer = function showEndDrawer () {
	endDrawer.open();
	content.style.display = 'none';
	style.style.display = 'none';
	script.style.display = 'none';
};

form.addEventListener('iron-form-presubmit', function() {
    this.request.method = 'put';
    this.request.body.content = content.value;
    this.request.body.style = style.value;
    this.request.body.script = script.value;
});

endDrawer.addEventListener('app-drawer-transitioned', function (event) {
	if (!endDrawer.opened) {
		content.style.display = 'flex';
		style.style.display = 'flex';
		script.style.display = 'flex';
	}	
});

ajaxResource.addEventListener('response', function () {
	content.value = ajaxResource.lastResponse.content || '';
	style.value = ajaxResource.lastResponse.style || '';
	script.value = ajaxResource.lastResponse.script || '';
});
