script:
Polymer({
	is: 'dialog-new-page',

    formResponse: function (event) {
		document.querySelector('#dialogNewPageModal').close();
		document.querySelector('#dialogNewPageForm').reset();
		this.dispatchEvent(new CustomEvent('new-page-created', {
			'bubbles': true,
			'detail': event.detail.response
		}));
    },

	save: function () {
		document.querySelector('#dialogNewPageForm').submit();
	},

	open: function () {
		document.querySelector('#dialogNewPageModal').open();	
	}
});

style:
app-toolbar {
    background-color: #2c5986;
    color: #fff;
}

paper-dialog {
	min-width: 360px;
}

paper-dialog app-header {
	margin: 0;
	padding: 0;
}
