script:
Polymer({
	is: 'dialog-new-page',

    listeners: {
      'iron-form-response': 'formResponse',
    },

    formResponse: function (event) {
		dialogNewPageModal.close();
		dialogNewPageForm.reset();
		this.fire('dialog-new-page-created', event.detail.response);
    },

	open: function () {
		dialogNewPageModal.open();	
	}
});

style:
app-toolbar {
    background-color: #2c5986;
    color: #fff;
}

paper-dialog {
	min-width: 360px;
}

paper-dialog app-header {
	margin: 0;
	padding: 0;
}
