style:
app-toolbar {
    background-color: #2c5986;
    color: #fff;
}

paper-dialog {
	min-width: 360px;
}

paper-dialog app-header {
	margin: 0;
	padding: 0;
}

script:
Polymer({
	is: 'dialog-new-component',

    listeners: {
      'iron-form-response': 'formResponse',
    },

    formResponse: function (event) {
		dialogNewComponentModal.close();
		dialogNewComponentForm.reset();
		this.fire('dialog-new-component-created', event.detail.response);
    },

	open: function () {
		dialogNewComponentModal.open();	
	}
});
