style:
app-toolbar {
    background-color: #2c5986;
    color: #fff;
}

paper-dialog {
	min-width: 360px;
	min-height: 364px;
	margin: 0;
	padding: 0;
}

paper-dialog app-header {
	margin: 0;
	padding: 0;
}

.installed-components-list {
	max-height: 300px;
    width: 600px;
    overflow: auto;
}

.search {
	margin: 8px 16px;
}

script:
Polymer({
	is: 'dialog-components',

	install: function (event) {
		var request = new XMLHttpRequest();
		request.open('POST', '/api/components', true);
		request.setRequestHeader('Content-Type', 'application/json');
		request.onreadystatechange = function () {
			if (request.readyState != 4 || request.status != 201) {
				return;
			}
			ajaxInstalledComponents.generateRequest();
		};
		request.send(JSON.stringify({
			'type': 'bower',
			'name': event.model.item.repo
		}));
	},

	delete: function (event) {
		var request = new XMLHttpRequest();
		request.open('DELETE', '/api/components/' + event.model.item._id, true);
		request.setRequestHeader('Content-Type', 'application/json');
		request.onreadystatechange = function () {
			if (request.readyState != 4 || request.status != 200) {
				return;
			}
			ajaxInstalledComponents.generateRequest();
		};
		request.send();
	},

	open: function () {
		dialogComponentsModel.open();	
	},

	properties: {
		selected: {
			type: 'Number',
			value: 0
		}
	}
});
