style:
.resource-list paper-item {
	min-height: 24px;
}

.resource-list paper-item h3 {
	margin: 7px 0;
}

paper-item {
    font-size: 9pt;
}

.paper-item-link {
    color: #000;
    text-decoration: none;
}

script:
Polymer({
	is: 'resource-list',

	url: function url(type, id) {
	    return '#/' + type + '/' + id;
	},

	createPage: function createPage() {
		this.dispatchEvent(new CustomEvent('create-page'));
	},

	createComponent: function createPage() {
		this.dispatchEvent(new CustomEvent('create-component'));
	},

	refresh: function refresh(type) {
		switch (type) {
			case 'components':
				this.querySelector('#resourceListComponents').generateRequest();
				break;

			case 'pages':
				this.querySelector('#resourceListPages').generateRequest();
				break;

			default:
				this.querySelector('#resourceListComponents').generateRequest();
				this.querySelector('#resourceListPages').generateRequest();
				break;
		}	
	}
});
