script:
Polymer({
	is: 'resource-info',

	_resourceTypeChanged: function (newValue) {
		this.isPage = newValue === 'pages'
	},

	save: function (event) {
		this.querySelector('#formResourceInfo').submit();
	},

	delete: function (event) {
		var request = new XMLHttpRequest();
		var self = this;
		request.open('DELETE', '/api/' + this.resourceType + '/' + this.resourceId, true);
		request.setRequestHeader('Content-Type', 'application/json');
		request.onreadystatechange = function () {
			if (request.readyState != 4 || request.status != 200) {
				return;
			}

			self.dispatchEvent(new CustomEvent('resource-info-deleted'));
		};
		request.send();
	},

	removeComponent: function (event) {
		var self = this;
		var index = this.resource.components.indexOf(event.detail._id);
		this.resource.components.splice(index, 1);

		var request = new XMLHttpRequest();
		request.open('PUT', '/api/' + this.resourceType + '/' + this.resourceId, true);
		request.setRequestHeader('Content-Type', 'application/json');
		request.onreadystatechange = function () {
			if (request.readyState != 4 || request.status != 200) {
				return;
			}

			self.dispatchEvent(new CustomEvent('resource-info-component-removed'));
		};
		request.send(JSON.stringify({
			components: this.resource.components
		}));
	},

	addComponent: function (event) {
		var self = this;
		if (!this.resource.components) {
			this.resource.components = [];
		}

		this.resource.components.push(event.detail._id);

		var request = new XMLHttpRequest();
		request.open('PUT', '/api/' + this.resourceType + '/' + this.resourceId, true);
		request.setRequestHeader('Content-Type', 'application/json');
		request.onreadystatechange = function () {
			if (request.readyState != 4 || request.status != 200) {
				return;
			}
			self.dispatchEvent(new CustomEvent('resource-info-component-added'));
		};
		request.send(JSON.stringify({
			components: this.resource.components
		}));
	},

	availableComponentsParam: function (item) {
		return encodeURIComponent(JSON.stringify({'_id': {
				'$nin': item || []
			}
		}));
	},

	addedComponentsParam: function (item) {
		return encodeURIComponent(JSON.stringify({'_id': {
				'$in': item || []
			}
		}));
	},

	onFormResourceInfoPresubmit: function (event) {
	    event.target.request.method = 'put';
	},

	onFormResourceInfoResponse: function (event) {
		this.dispatchEvent(new CustomEvent('resource-info-saved'));
	},

	properties: {
		isPage: {
			type: Boolean,
			value: false
		},
		resource: {
			type: Object
		},
		resourceId: {
			type: String
		},
		resourceType: {
			type: String,
    		observer: '_resourceTypeChanged'
		}
	}
});

style:
:host {
	text-align: left;
}

paper-material {
	margin: 16px;
}

.danger {
  color: red;
}

paper-item {
	min-height: 24px;
